module.exports = function (Super, init) {
    var N, NEW;
    N = function () {
    };
    init = init || N;
    N.prototype = Super.prototype;
    NEW = function () {
        return new NEW.fz.initialize(arguments);
    };
    NEW.fz = NEW.prototype = new N();
    NEW.fz.initialize = function (M) {
        init.apply(this, M);
        return this;
    };
    NEW.fz.constructor = NEW;
    NEW.fz.initialize.prototype = NEW.fz;
    NEW.initialize = init;
    return NEW;
};
